import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import './Auth.css';

// Create our own logo component for the login page
const WildEyeLogo = () => (
  <svg width="30" height="30" viewBox="0 0 36 36" fill="none" xmlns="http://www.w3.org/2000/svg">
    {/* Eye outline */}
    <ellipse cx="18" cy="18" rx="15" ry="12" stroke="currentColor" strokeWidth="2.5" />
    
    {/* Pupil */}
    <circle cx="18" cy="18" r="6" fill="currentColor" />
    
    {/* Light reflection */}
    <circle cx="15" cy="15" r="1.5" fill="white" />
    
    {/* Eyelashes/rays */}
    <path d="M4 10L7 12" stroke="currentColor" strokeWidth="2" strokeLinecap="round" />
    <path d="M4 26L7 24" stroke="currentColor" strokeWidth="2" strokeLinecap="round" />
    <path d="M32 10L29 12" stroke="currentColor" strokeWidth="2" strokeLinecap="round" />
    <path d="M32 26L29 24" stroke="currentColor" strokeWidth="2" strokeLinecap="round" />
    <path d="M18 2V5" stroke="currentColor" strokeWidth="2" strokeLinecap="round" />
    <path d="M18 31V34" stroke="currentColor" strokeWidth="2" strokeLinecap="round" />
    
    {/* Animal silhouette in pupil */}
    <path d="M20 17C20.5 16 21.5 16 22 17" stroke="white" strokeWidth="0.75" strokeLinecap="round" />
    <path d="M14 17C14.5 16 15.5 16 16 17" stroke="white" strokeWidth="0.75" strokeLinecap="round" />
    <path d="M16 19C17 20 19 20 20 19" stroke="white" strokeWidth="0.75" strokeLinecap="round" />
  </svg>
);

const Auth: React.FC = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    name: '',
  });
  const navigate = useNavigate();

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    // TODO: Implement actual authentication
    console.log('Form submitted:', formData);
    navigate('/dashboard');
  };

  const handleGoogleAuth = () => {
    // TODO: Implement Google OAuth authentication
    console.log('Google Auth initiated');
    // This would typically involve redirecting to Google's OAuth flow
    // For now, we'll just navigate to the dashboard as a placeholder
    navigate('/dashboard');
  };

  return (
    <div className="auth-container">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
        className="auth-card"
      >
        <div className="logo-container">
          <Link to="/">
            <WildEyeLogo />
          </Link>
        </div>
        
        <div className="auth-header">
          <h2>{isLogin ? 'Welcome Back' : 'Join WildEye'}</h2>
          <p>{isLogin ? 'Sign in to continue your conservation journey' : 'Help us protect wildlife with technology'}</p>
        </div>

        <form onSubmit={handleSubmit} className="auth-form">
          {!isLogin && (
            <div className="form-group">
              <label htmlFor="name">Full Name</label>
              <input
                type="text"
                id="name"
                name="name"
                value={formData.name}
                onChange={handleChange}
                placeholder="Enter your name"
                required
              />
            </div>
          )}

          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              placeholder="Enter your email"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              placeholder="Enter your password"
              required
            />
          </div>

          <button type="submit" className="auth-button">
            <svg width="16" height="16" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
              <path d="M9 11L11 13L15 9" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
              <path d="M12 22C17.5228 22 22 17.5228 22 12C22 6.47715 17.5228 2 12 2C6.47715 2 2 6.47715 2 12C2 17.5228 6.47715 22 12 22Z" stroke="currentColor" strokeWidth="2"/>
            </svg>
            {isLogin ? 'Sign In' : 'Create Account'}
          </button>
        </form>

        <div className="social-auth">
          <span>Or continue with</span>
        </div>

        <button onClick={handleGoogleAuth} className="google-button">
          <img src="data:image/png;base64," alt="Google logo" />
          Continuw with G<img src="data:image/png;base64," style={{display: 'inline', width: '14px', height: '14px', margin: '0 2px', verticalAlign: 'middle'}} />oogle
        </button>

        <div className="auth-footer">
          <p>
            {isLogin ? "Don't have an account?" : "Already have an account?"}
            <button
              className="toggle-button"
              onClick={() => setIsLogin(!isLogin)}
            >
              {isLogin ? 'Sign Up' : 'Sign In'}
            </button>
          </p>
        </div>
      </motion.div>
    </div>
  );
};

export default Auth; 